import osfrom django.conf.urls.defaults import *from django.conf import settings ## STATIC *MUST* BE SERVED BY APACHE#urlpatterns = patterns('',)def statics_wrapper(request, **dict):    from django.views import static    return static.serve(request, dict['path'], document_root = os.path.join(settings.BASE_DIR, 'apps', dict['app'], 'static'), show_indexes=True)    urlpatterns += patterns('', (r'^apps/(?P<app>[^/]+)/static/(?P<path>.+)$', statics_wrapper))urlpatterns += patterns('',    (r'^core/static/(?P<path>.*)/?$', 'django.views.static.serve', {'document_root':settings.MEDIA_ROOT, 'show_indexes':True}), )  # urlpatterns += patterns('',    # (r'^static/session/(?P<path>.*)/?$', 'django.views.static.serve', {'document_root':whiteboard_settings.allowed_paths['public'], 'show_indexes':True}), # )